    public static int[] roll(int rolls) {
        Random rnd = new Random();
        int[] numsRolled = new int[rolls];
        System.out.println(numsRolled + " Arr");
        for (int i = 0; i < numsRolled.length; i++) {
            numsRolled[i] = rnd.nextInt((6 - 1) + 1) + 1;
            System.out.println(i);
        }

        return numsRolled;
    }

